import React from "react";
import * as myProjectService from "../services/myProjectService";

import { Card, CardBody, CardFooter, Row, Col } from "reactstrap";

import CardAuthor from "components/CardElements/CardAuthor.jsx";
import Button from "components/CustomButton/CustomButton.jsx";

class YogaPerson extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      myProject: []
    };
  }

  componentDidMount() {
    console.log("mounted");
    myProjectService
      .getAllProfiles()
      .then(this.onGetAllSuccess)
      .catch(this.onError);
  }

  onGetAllSuccess = response => {
    console.log("successful get all profiles");
    this.setState({
      myProject: response.data.items
    });

    console.log(response.data.items);
  };

  onDeleteSuccess = () => {};

  onError = () => {
    console.log("error");
  };

  onProfileUpdate = (e, yoga) => {
    debugger;
    let id = yoga.id;
    this.props.history.push("/register/" + id, yoga);
  };

  onProfileDelete(e, yoga) {
    debugger;
    let id = yoga.id;

    myProjectService
      .deleteProfile(id)
      .then(response => {
        let list = [...this.state.myProject];
        let newList = list.filter(object => object.id !== yoga.id);
        this.setState({ myProject: [...newList] }, () =>
          console.log(this.state)
        );
      })
      .catch(this.onError);
  }

  mapProject = yoga => {
    return (
      <React.Fragment key={yoga.id}>
        <Col md={4}>
          <Card className="card-user">
            <div className="image">
              <img
                src={
                  "https://images.pexels.com/photos/262325/pexels-photo-262325.jpeg?auto=compress&cs=tinysrgb&dpr=2&w=500"
                }
                alt="..."
              />
            </div>
            <CardBody>
              <CardAuthor
                avatar={yoga.image}
                avatarAlt="..."
                title={yoga.firstName + " " + yoga.lastName}
              />
              <p className="description text-center">
                {" "}
                Certifications: {yoga.certifications}
              </p>
              <p className="description text-center">
                {" "}
                Specializations: {yoga.specializations}
              </p>
              <p className="description text-center">
                {" "}
                Yoga Style: {yoga.yogaStyle}
              </p>
              <p className="description text-center"> Rates: {yoga.rates}</p>
              <p className="description text-center">
                {" "}
                Location: {yoga.location}
              </p>

              <p className="description text-center">Bio: {yoga.bio}</p>
            </CardBody>
            <hr />
            <Col lg={12} className="mr-auto   text-center">
              <h7>
                Website:
                <br />
                <small>{yoga.website}</small>
              </h7>
            </Col>

            <CardFooter>
              <hr />
              <div className="button-container">
                <Row>
                  <Col xs={6} sm={6} md={6} lg={6} className="ml-auto">
                    <h7>
                      Email:
                      <br />
                      <small>{yoga.email}</small>
                    </h7>
                  </Col>
                  <Col xs={6} sm={6} md={6} lg={4} className="mr-auto ml-auto">
                    <h7>
                      Phone:
                      <br />
                      <small>{yoga.phone}</small>
                    </h7>
                  </Col>
                </Row>
              </div>

              <hr />
              <Row>
                <div
                  onClick={e => this.onProfileUpdate(e, yoga)}
                  className=" ml-auto mr-auto"
                >
                  <Button size="sm" color="success">
                    Update
                  </Button>
                </div>
                <div
                  onClick={e => this.onProfileDelete(e, yoga)}
                  className=" ml-auto mr-auto"
                >
                  <Button size="sm" color="danger">
                    Delete
                  </Button>
                </div>
              </Row>
            </CardFooter>
          </Card>
        </Col>
      </React.Fragment>
    );
  };

  render() {
    const { data, orderBy } = this.props;
    const input = this.state.myProject; // array from the bottom of this script
    const output = input.map(item => {
      return (
        <div>
          <small className={orderBy === item ? "active" : null}>{item}:</small>{" "}
          {data[item]}
        </div>
      );
    });
    const profileList = this.state.myProject.map(this.mapProject);
    return (
      <div>
        <Col md={4}>
          <Card className="card-user">
            <div className="image">
              <img
                src={
                  "https://images.pexels.com/photos/262325/pexels-photo-262325.jpeg?auto=compress&cs=tinysrgb&dpr=2&w=500"
                }
                alt="..."
              />
            </div>
            <CardBody>
              <CardAuthor
                avatar={data.image}
                avatarAlt="..."
                title={data.firstName + " " + data.lastName}
              />
              {output}
              <p className="description text-center">
                {" "}
                Certifications: {data.certifications}
              </p>
              <p className="description text-center">
                {" "}
                Specializations: {data.specializations}
              </p>
              <p className="description text-center">
                {" "}
                Yoga Style: {data.yogaStyle}
              </p>
              <p className="description text-center"> Rates: {data.rates}</p>
              <p className="description text-center">
                {" "}
                Location: {data.location}
              </p>

              <p className="description text-center">Bio: {data.bio}</p>
            </CardBody>
            <hr />
            <Col lg={12} className="mr-auto   text-center">
              <h7>
                Website:
                <br />
                <small>{data.website}</small>
              </h7>
            </Col>

            <CardFooter>
              <hr />
              <div className="button-container">
                <Row>
                  <Col xs={6} sm={6} md={6} lg={6} className="ml-auto">
                    <h7>
                      Email:
                      <br />
                      <small>{data.email}</small>
                    </h7>
                  </Col>
                  <Col xs={6} sm={6} md={6} lg={4} className="mr-auto ml-auto">
                    <h7>
                      Phone:
                      <br />
                      <small>{data.phone}</small>
                    </h7>
                  </Col>
                </Row>
              </div>

              <hr />
              <Row>
                <div
                  onClick={e => this.onProfileUpdate(e, data)}
                  className=" ml-auto mr-auto"
                >
                  <Button size="sm" color="success">
                    Update
                  </Button>
                </div>
                <div
                  onClick={e => this.onProfileDelete(e, data)}
                  className=" ml-auto mr-auto"
                >
                  <Button size="sm" color="danger">
                    Delete
                  </Button>
                </div>
              </Row>
            </CardFooter>
          </Card>
        </Col>
      </div>
    );
  }
}

export default YogaPerson;
