import React from "react";

class Dropdown extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      firstName: "",
      lastName: "",
      certifications: "",
      specializations: "",
      yogaStyle: "",
      rates: "",
      location: ""
    };
  }

  render() {
    const {
      dropdownActive,
      toggle,
      orderBy,
      order,
      doOrderBy,
      doOrder
    } = this.props;
    const checked = <span className="glyphicon glyphicon-ok" />;
    const names = [
      ["firstName", "first name"],
      ["lastName", "last name"],
      ["certifications", "certifications"],
      ["specializations", "specializations"],
      ["yogaStyle", "yoga style"],
      ["rates", "rates"],
      ["location", "location"]
    ];

    const input = names; // array from the bottom of this script
    const output = names.map(item => {
      return (
        <li>
          <a href="#" onClick={doOrderBy} data-value={item[0]}>
            {item[1]} {orderBy === item[0] ? checked : null}
          </a>
        </li>
      );
    });

    return (
      <div
        className={
          dropdownActive ? "dropdown  pull-right open" : "dropdown pull-right "
        }
      >
        <a className="btn btn-info" onClick={toggle} href="#">
          Sort items by
          <span className="caret" />
        </a>
        <ul className="dropdown-menu">
          {output}
          <li className="divider" />
          <li>
            <a href="#" onClick={doOrder} data-value="asc">
              ascendind {order === "asc" ? checked : null}
            </a>
          </li>
          <li>
            <a href="#" onClick={doOrder} data-value="desc">
              descending {order === "desc" ? checked : null}
            </a>
          </li>
        </ul>
      </div>
    );
  }
}

// firstName: "",
// lastName: "",
// certifications: "",
// specializations: "",
// yogaStyle: "",
// bio: "",
// rates: "",
// location: "",
// website: "",
// email: "",
// phone: "",
// image: ""

const names = [
  ["firstName", "first name"],
  ["lastName", "last name"],
  ["certifications", "certifications"],
  ["specializations", "specializations"],
  ["yogaStyle", "yoga style"],
  ["rates", "rates"],
  ["location", "location"]
];
// const categories = ["company","job","city","gender"]
