import React from "react";
import * as myProjectService from "../services/myProjectService";

import { Card, CardBody, CardFooter, Row, Col } from "reactstrap";
import PropTypes from "prop-types";

import CardAuthor from "components/CardElements/CardAuthor.jsx";
// import FormInputs from "components/FormInputs/FormInputs.jsx";
import Button from "components/CustomButton/CustomButton.jsx";

import damirBosnjak from "assets/img/damir-bosnjak.jpg";
// import Register from "components/Register";

class Articles extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      yogaArticles: []
    };
  }

  //   componentDidMount() {
  //     console.log("mounted");
  //     myProjectService
  //       .getAllProfiles()
  //       .then(this.onGetAllSuccess)
  //       .catch(this.onError);
  //   }

  //   onGetAllSuccess = response => {
  //     console.log("successful get all profiles");
  //     // debugger;
  //     this.setState({
  //       myProject: response.data.items
  //     });

  //     console.log(response.data.items);
  //   };

  //   onError = () => {
  //     console.log("get all profiles error");
  //   };

  // onSubmit = e => {
  //   e.preventDefault();
  //   // console.log(values);
  //   debugger;
  //   const payload = this.props.data;
  //   console.log(payload);
  //   myProjectService
  //     .submitProfile(payload)
  //     .then(this.onSuccess)
  //     .catch(this.onError);
  // };

  // onSuccess = () => {
  //   console.log("successful ya");
  //   // console.log(this.props.match.params.id);
  // };

  // onError = () => {
  //   console.log("boo boo");
  // };
  componentDidMount() {
    debugger;

    myProjectService
      .getYogaArticles()
      .then(this.onSuccess)
      .catch(this.onError);
  }

  onSuccess = resp => {
    console.log("successs get articles");
    console.log(resp.data.items);
    this.setState({
      yogaArticles: resp.data.items
    });
  };

  onError = () => {
    console.log("Error on get articles");
  };

  mapArticles = articles => {
    console.log("articles", articles);
    const path = "https://www.gaiam.com" + articles.link;
    return (
      <React.Fragment>
        <Col md={6}>
          <Card className="card-user">
            <CardBody>
              <Row>
                <div className="mr-auto   text-center">
                  <img src={articles.image} alt="..." />
                </div>

                <div className="author">
                  <a href={this.props.link ? this.props.link : "#"}>
                    <br />
                    <br />
                    <br />
                    <br />
                    <img src={this.props.avatar} alt={this.props.avatarAlt} />
                    <h5 className="title">{articles.title} </h5>
                  </a>
                  <p className="description">{this.props.description}</p>
                </div>

                {/* <CardAuthor title={articles.title} /> */}
                {/* <p className="description text-center">{articles.title}</p> */}
                <p className="description text-center" md="col-5">
                  {articles.excerpt}
                </p>
                <a href={path}>Read More</a>
              </Row>
            </CardBody>
          </Card>
        </Col>
        {/* </Row> */}

        {/* </Col> */}
      </React.Fragment>
    );
  };

  render() {
    const articleList = this.state.yogaArticles.map(this.mapArticles);
    return (
      <React.Fragment>
        <div className="content ">
          <Row>{articleList}</Row>
        </div>
      </React.Fragment>
    );
  }
}

// CardAuthor.propTypes = {
//   // Where the user to be redirected on clicking the avatar
//   link: PropTypes.string,
//   avatar: PropTypes.string,
//   avatarAlt: PropTypes.string,
//   title: PropTypes.oneOfType([PropTypes.string, PropTypes.node]),
//   description: PropTypes.oneOfType([PropTypes.string, PropTypes.node])
// };

export default Articles;
