          {myProject[item]}
        </div>
      );
    });
    return (
      <div
        className="media"
        style={{ objectFit: "contain" }}
        height="2"
        width="2"
      >
        <div className="media-left">
          <img
            className="media-object"
            src={myProject.image}
            alt={`${myProject.firstName} ${myProject.lastName}`}
