import React from "react";
import * as myProjectService from "../services/myProjectService";
import {
  Card,
  CardHeader,
  CardBody,
  CardTitle,
  // CardFooter,
  Row
  // Col
} from "reactstrap";

// import CardAuthor from "components/CardElements/CardAuthor.jsx";
import FormInputs from "components/FormInputs/FormInputs.jsx";
import Button from "components/CustomButton/CustomButton.jsx";

class Register extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      firstName: "",
      lastName: "",
      certifications: "",
      specializations: "",
      yogaStyle: "",
      bio: "",
      rates: "",
      location: "",
      website: "",
      email: "",
      phone: ""
    };
  }

  handleChange = e => {
    this.setState({
      [e.target.name]: e.target.value
    });
  };

  onSubmit = e => {
    e.preventDefault();
    // console.log(values);
    debugger;
    const payload = {
      firstName: this.state.firstName,
      lastName: this.state.lastName,
      certifications: this.state.certifications,
      specializations: this.state.specializations,
      yogaStyle: this.state.yogaStyle,
      bio: this.state.bio,
      rates: this.state.rates,
      location: this.state.location,
      image: this.state.image,
      website: this.state.website,
      email: this.state.email,
      phone: this.state.phone
    };

    myProjectService
      .submitProfile(payload)
      .then(this.onSuccess)
      .catch(this.onError);
  };

  onSuccess = () => {
    console.log("successful ya");
  };

  onError = () => {
    console.log("boo boo");
  };

  render() {
    return (
      <React.Fragment>
        {/* <br />
        <br />
        <br /> */}

        <div className="content-wrapper">
          <div className="content-heading" />

          <div className="row">
            <div className="col-xl-12">
              <div className="card card-default">
                <div className="card-body">
                  {/* Flat make resvervation */}
                  <section className="flat-row contact-2">
                    <div className="container">
                      <div className="row">
                        <div className="col-md-12">
                          <div className="reservation-page-right">
                            {/* <img
                              // src="https://images.pexels.com/photos/395198/pexels-photo-395198.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=750&w=1260"
                              // src="https://www.yogatoday.com/files/images/uploads/made/files/images/uploads/poses/PD073AA_White_Steph_Warrior2_018Q5353_WEB_1300_725_s_c1.jpg"
                              className="img-responsive"
                              alt="reservation-banner"
                            /> */}
                          </div>
                        </div>
                        {/*col-md-5*/}
                        <div className="col-md-12">
                          <div className="reservation-page-center">
                            <div className="reservation-page-form">
                              <div className="title-section">
                                <h1 className="title">Register</h1>
                              </div>
                              <form
                                id="reservation-form"
                                action="contact/contact-process.php"
                                noValidate="novalidate"
                              >
                                <div className="reservation-page-input-box">
                                  <label>First Name</label>
                                  <input
                                    type="text"
                                    className="form-control"
                                    placeholder="First Name"
                                    name="firstName"
                                    id="form-firstName"
                                    data-error="Subject field is required"
                                    required
                                    values={this.state.firstName}
                                    onChange={e => this.handleChange(e)}
                                  />
                                </div>
                                <div className="reservation-page-input-box">
                                  <label>Last Name</label>
                                  <input
                                    type="text"
                                    className="form-control"
                                    placeholder="Last Name"
                                    name="lastName"
                                    id="form-lastName"
                                    data-error="Subject field is required"
                                    required
                                    values={this.state.lastName}
                                    onChange={e => this.handleChange(e)}
                                  />
                                </div>
                                <div className="reservation-page-input-box">
                                  <label>Certifications</label>
                                  <input
                                    type="email"
                                    className="form-control"
                                    placeholder="Enter your certifications"
                                    name="certifications"
                                    id="form-certifications"
                                    data-error="Subject field is required"
                                    required
                                    values={this.state.certifications}
                                    onChange={e => this.handleChange(e)}
                                  />
                                </div>
                                <div className="reservation-page-input-box">
                                  <label>Specializations</label>
                                  <input
                                    type="text"
                                    className="form-control"
                                    placeholder="Your specializations"
                                    name="specializations"
                                    id="form-specializations"
                                    data-error="Specializations field is required"
                                    required
                                    values={this.state.specializations}
                                    onChange={e => this.handleChange(e)}
                                  />
                                </div>
                                <div className="reservation-page-input-box">
                                  <label>Yoga Style</label>
                                  <input
                                    type="text"
                                    className="form-control"
                                    placeholder="Enter style of Yoga"
                                    name="yogaStyle"
                                    id="form-yogaStyle"
                                    data-error="Yoga Style field is required"
                                    required
                                    values={this.state.yogaStyle}
                                    onChange={e => this.handleChange(e)}
                                  />
                                </div>

                                <div className="reservation-page-textarea">
                                  <label>Bio</label>
                                  <textarea
                                    className="form-control your-bio"
                                    placeholder="Write a brief bio about yourself.."
                                    name="bio"
                                    id="form-bio"
                                    defaultValue={""}
                                    values={this.state.bio}
                                    onChange={e => this.handleChange(e)}
                                  />
                                </div>

                                <div className="reservation-page-input-box">
                                  <label>Rates</label>
                                  <input
                                    type="text"
                                    className="form-control"
                                    placeholder="Enter rates"
                                    name="rates"
                                    id="form-rates"
                                    data-error="Rates field is required"
                                    required
                                    values={this.state.rates}
                                    onChange={e => this.handleChange(e)}
                                  />
                                </div>

                                <div className="reservation-page-input-box">
                                  <label>Location</label>
                                  <input
                                    type="text"
                                    className="form-control"
                                    placeholder="Enter your location"
                                    name="location"
                                    id="form-location"
                                    data-error="Location field is required"
                                    required
                                    values={this.state.location}
                                    onChange={e => this.handleChange(e)}
                                  />
                                </div>

                                <div className="reservation-page-input-box">
                                  <label>Image</label>
                                  <input
                                    type="text"
                                    className="form-control"
                                    placeholder="Enter image URL"
                                    name="image"
                                    id="form-image"
                                    data-error="Image field is required"
                                    required
                                    values={this.state.image}
                                    onChange={e => this.handleChange(e)}
                                  />
                                </div>

                                <div className="reservation-page-input-box">
                                  <label>Website</label>
                                  <input
                                    type="text"
                                    className="form-control"
                                    placeholder="Enter website URL"
                                    name="website"
                                    id="form-website"
                                    data-error="Website field is required"
                                    required
                                    values={this.state.website}
                                    onChange={e => this.handleChange(e)}
                                  />
                                </div>

                                <div className="reservation-page-input-box">
                                  <label>Email</label>
                                  <input
                                    type="text"
                                    className="form-control"
                                    placeholder="Enter your email"
                                    name="email"
                                    id="form-email"
                                    data-error="Email field is required"
                                    required
                                    values={this.state.email}
                                    onChange={e => this.handleChange(e)}
                                  />
                                </div>

                                <div className="reservation-page-input-box">
                                  <label>Phone</label>
                                  <input
                                    type="text"
                                    className="form-control"
                                    placeholder="Enter your phone number"
                                    name="phone"
                                    id="form-phone"
                                    data-error="Phone number field is required"
                                    required
                                    values={this.state.phone}
                                    onChange={e => this.handleChange(e)}
                                  />
                                </div>

                                <br />
                                <div className="reservation-booking">
                                  <button
                                    type="submit"
                                    className="book-now-btn"
                                    onClick={this.onSubmit}
                                  >
                                    SUBMIT
                                  </button>
                                  <br />
                                  <br />
                                </div>
                              </form>
                            </div>
                          </div>
                        </div>
                        {/*col-md-7*/}
                      </div>
                      {/*row*/}
                    </div>
                    {/*container */}
                  </section>
                </div>
              </div>
            </div>
          </div>
        </div>

        <Card className="card-user">
          <CardHeader>
            <CardTitle className="title text-center">Register</CardTitle>
          </CardHeader>
          <CardBody>
            <form>
              <FormInputs
                // type="text"
                // className="form-control"
                // placeholder="First Name"
                name="firstName"
                id="form-firstName"
                data-error="Subject field is required"
                required
                values={this.state.firstName}
                onChange={e => this.handleChange(e)}
                ncols={["col-md-6 pr-1", "col-md-6 pl-1"]}
                proprieties={[
                  {
                    label: "First Name",
                    inputProps: {
                      type: "text",
                      placeholder: "First Name",
                      defaultValue: "Chet"
                    }
                  }
                ]}
              />

              <FormInputs
                // type="text"
                // className="form-control"
                // placeholder="First Name"
                name="lastName"
                id="form-lastName"
                data-error="Subject field is required"
                required
                values={this.state.lastName}
                onChange={e => this.handleChange(e)}
                proprieties={[
                  {
                    label: "Last Name",
                    inputProps: {
                      type: "text",
                      placeholder: "Last Name",
                      defaultValue: "Faker"
                    }
                  }
                ]}
              />
              <FormInputs
                ncols={["col-md-5 pr-1", "col-md-3 px-1", "col-md-4 pl-1"]}
                proprieties={[
                  {
                    label: "Website",
                    inputProps: {
                      type: "text",
                      // disabled: true,
                      defaultValue: "Enter URL.."
                    }
                  },
                  {
                    label: "Phone number",
                    inputProps: {
                      type: "text",
                      defaultValue: "xxx-xxx-xxxx"
                    }
                  },
                  {
                    label: "Email address",
                    inputProps: {
                      type: "email",
                      placeholder: "Email"
                    }
                  }
                ]}
              />

              <FormInputs
                ncols={["col-md-12"]}
                proprieties={[
                  {
                    label: "Certifications",
                    inputProps: {
                      type: "text",
                      placeholder: "ex. 200hr, 500hr.."
                      // defaultValue: ""
                    }
                  }
                ]}
              />
              <FormInputs
                ncols={["col-md-12"]}
                proprieties={[
                  {
                    label: "Specializations",
                    inputProps: {
                      type: "text",
                      placeholder: "ex. Prenatal.."
                      // defaultValue: ""
                    }
                  }
                ]}
              />

              <FormInputs
                ncols={["col-md-4 pr-1", "col-md-4 px-1", "col-md-4 pl-1"]}
                proprieties={[
                  {
                    label: "Yoga Style",
                    inputProps: {
                      type: "text",
                      // defaultValue: "Melbourne",
                      placeholder: "ex. Restorative Yoga.."
                    }
                  },
                  {
                    label: "Rates",
                    inputProps: {
                      type: "text",
                      // defaultValue: "Australia",
                      placeholder: "ex. $125/hr"
                    }
                  },
                  {
                    label: "Location",
                    inputProps: {
                      type: "text",
                      placeholder: "City or Zip Code"
                    }
                  }
                ]}
              />
              <FormInputs
                ncols={["col-md-12"]}
                proprieties={[
                  {
                    label: "Bio",
                    inputProps: {
                      type: "textarea",
                      // defaultValue:
                      //   "Oh so, your weak rhyme You doubt I'll bother, reading into it",
                      placeholder: "Enter brief bio.."
                    }
                  }
                ]}
              />
              <Row>
                <div className="update ml-auto mr-auto">
                  <Button color="primary" round>
                    Update Profile
                  </Button>
                </div>
              </Row>
            </form>
          </CardBody>
        </Card>
      </React.Fragment>
    );
  }
}

export default Register;
