import React from "react";
// import "../../src/style/dropdown.css";

class Dropdown extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      myProject: []
      // firstName: "",
      // lastName: "",
      // certifications: "",
      // specializations: "",
      // yogaStyle: "",
      // rates: "",
      // location: ""
    };
  }
  renderDropdown = () => {
    debugger;
    if (this.props.dropdownActive === true) {
      return (
        <ul className="dropdown-menu">
          {this.props.output}
          <li className="divider" />
          <li>
            <a href="#" onClick={this.props.oOrder} data-value="asc">
              ascending {this.props.order === "asc" ? this.props.checked : null}
            </a>
          </li>
          <li>
            <a href="#" onClick={this.props.doOrder} data-value="desc">
              descending{" "}
              {this.props.order === "desc" ? this.props.checked : null}
            </a>
          </li>
        </ul>
      );
    } else {
      return null;
    }
  };

  render() {
    const {
      dropdownActive,
      toggle,
      orderBy,
      order,
      doOrderBy,
      doOrder
    } = this.props;
    const checked = <span className="glyphicon glyphicon-ok" />;
    const names = [
      ["firstName", "first name"],
      ["lastName", "last name"],
      ["certifications", "certifications"],
      ["specializations", "specializations"],
      ["yogaStyle", "yoga style"],
      ["rates", "rates"],
      ["location", "location"]
    ];

    const input = names; // array from the bottom of this script
    const output = names.map(item => {
      return (
        <li>
          <a href="#" onClick={doOrderBy} data-value={item[0]}>
            {item[1]} {orderBy === item[0] ? checked : null}
          </a>
        </li>
      );
    });

    // let renderDropdown = () => {
    //   debugger;
    //   if (dropdownActive === true) {
    //     return (
    //       <ul className="dropdown-menu">
    //         {output}
    //         <li className="divider" />
    //         <li>
    //           <a href="#" onClick={doOrder} data-value="asc">
    //             ascending {order === "asc" ? checked : null}
    //           </a>
    //         </li>
    //         <li>
    //           <a href="#" onClick={doOrder} data-value="desc">
    //             descending {order === "desc" ? checked : null}
    //           </a>
    //         </li>
    //       </ul>
    //     );
    //   } else {
    //     return null;
    //   }
    // };
    return (
      <div className="dropdown  ">
        <a className="btn btn-info" onClick={toggle} href="#">
          Sort items by
          <span className="caret" />
        </a>
        <ul className="dropdown-menu">
          {this.props.output}
          <li className="divider" />
          <li>
            <a href="#" onClick={this.props.oOrder} data-value="asc">
              ascending {this.props.order === "asc" ? this.props.checked : null}
            </a>
          </li>
          <li>
            <a href="#" onClick={this.props.doOrder} data-value="desc">
              descending{" "}
              {this.props.order === "desc" ? this.props.checked : null}
            </a>
          </li>
        </ul>
      </div>
      //   <div
      //     className={
      //       dropdownActive ? "dropdown  pull-right open" : "dropdown pull-right "
      //     }
      //   >
      //     <a className="btn btn-info" onClick={toggle} href="#">
      //       Sort items by
      //       <span className="caret" />
      //     </a>
      //     <ul className="dropdown-menu">
      //       {output}
      //       <li className="divider" />
      //       <li>
      //         <a href="#" onClick={doOrder} data-value="asc">
      //           ascending {order === "asc" ? checked : null}
      //         </a>
      //       </li>
      //       <li>
      //         <a href="#" onClick={doOrder} data-value="desc">
      //           descending {order === "desc" ? checked : null}
      //         </a>
      //       </li>
      //     </ul>
      //   </div>
    );
  }
}

